package com.besant.loops;

public class Loops_in_java {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println(" While Loop ");
		int f = 0 ; // Initializing an value
		while( f <= 10) {
//			System.out.println( f + " "); // println :- here ln means it prints every sentence in new line 
			System.out.print(f + " ");
			f++; //increment operator 
		}
		
		System.out.println();
		System.out.print(" Do While Loop ");
		int d = 3;
		do {
			System.out.println(d + " ");
		}while( d >= 10 );
		
		System.out.println();
		System.out.println(" For Loop ");{
			for ( int z = 0;  z <= 10; z++) {
				System.out.print(z + " ");
			}
		}
		//Create a code to get multiplication table using any of the loops 
				//Print characters from 'a' to 'z'
		}

}
